import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import * as BooksAPI from "../BooksAPI";
import Shelf from './Shelf';

const BookShelf = () => {
    const [books, setBooks] = useState([]);
    const [mapOfIdToBooks, setMapOfIdToBooks] = useState(new Map());
    const [searchBooks, setSearchBooks] = useQuery(query);

    useEffect(() => {

      BooksAPI.getAll()
        .then(data => {
          setBooks(data)
          setMapOfIdToBooks(createMapOfBooks(data))
        }
        );
    }, [])


    useEffect(() => {

      const combined = searchBooks.map(book => {
        if (mapOfIdToBooks.has(book.id)) {
          return mapOfIdToBooks.get(book.id);
        } else {
          return book;
        }
      })
      setMergedBooks(combined);
    }, [searchBooks])


    const createMapOfBooks = (books) => {
      const map = new Map();
      books.map(book => map.set(book.id, book));
      return map;
    }

    const updateBookShelf = (book, whereTo) => {
      const updatedBooks = books.map(b => {
        if (b.id === book.id) {
          book.shelf = whereTo;
          return book;
        }
        return b;
      })
      if (!mapOfIdToBooks.has(book.id)) {
        book.shelf = whereTo;
        updatedBooks.push(book)
      }
      setBooks(updatedBooks);
      BooksAPI.update(book, whereTo);
    }

    // const compartments = [
    //   { type: "currentlyReading", title: "Currently Reading" },
    //   { type: "wantToRead", title: "Want to Read" },
    //   { type: "read", title: "Read" },
    // ];

    const currentlyReading = books.filter((book) => book.shelf === "currentlyReading");
    const whatToRead = books.filter((book) => book.shelf === "wantToRead");
    const read = books.filter((book) => book.shelf === "read");

return (
    <div className="app">
        <div className="list-books">
           <div className="list-books-content">
           {books.length > 0 && (
          <div>
            {/* {compartments.map((compartment, index) => {
              const compartmentBooks = books.filter(
                (book) => book.shelf === compartment.type
              );
              return (
                <div className="bookshelf" key={index}>
                  <h2 className="bookshelf-title">{compartment.title}</h2>
                  <div>
                    <div className="bookshelf-books" key={compartment}>
                      <ol className="books-grid">
                        {compartmentBooks.map((book) => (
                          <Book key={book.id} book={book} changeShelf={changeShelf} />
                        ))}
                      </ol>
                    </div>
                  </div>
                </div>
              );
            })} */}
            <Shelf title="Currently Reading" books={currentlyReading} updateBookShelf={updateBookShelf}/>
            <Shelf title="Want To Read" books={whatToRead} updateBookShelf={updateBookShelf}/>
            <Shelf title="Read" books={read} updateBookShelf={updateBookShelf}/>
          </div>
        )}
           </div>
           <div className="open-search">
            {/* <Link to={{
                pathname: '/search',
                state: {
                  currentBooks: books
                }
            }}
            /> */}
            <Link to="/search">
            </Link>
          </div>
        </div>
    </div>
    );
}

export default BookShelf;
